<div id="content">    <div id="company">        <div id="menu-list">            <a href="/comments" class="menu-list-item ">Отзывы</a>            <a href="/news" class="menu-list-item active">Новости</a>            <a href="/legal" class="menu-list-item ">Правовая информация</a>            <div style="clear: both;"></div>        </div>        <div id="news-list">            <? foreach ($array['content']['content'] as $key => $val){ ?>                <?                    $filter = array("content_id" => $val["id"]);                    $images = Element::SelectAll('files', $filter, null, null);                ?>                <a class="news-item" href="/news/<?=$val['id']?>/" id="<?=$key;?>">                    <div class="news-item-img">                        <img src="/<?=$images[0]['path']?>"/>                    </div>                    <div class="news-item-text">                        <div class="news-item-text-inner">                            <div class="news-item-text-zagol"><?=$val['title']?></div>                            <?=$val['desc']?>                        </div>                    </div>                    <div style="clear:both;"></div>                </a>            <?}?>            <div style="clear: both;"></div>        </div>    </div></div>